import React, {Component} from 'react';
import  './SearchPanel.css';

class SearchPanel extends Component {

	state = {
		term: ''
	};

	render() {
		return (
		<input 
			type='text' 
			className="form-control search-input" 
			placeholder = 'type to search'
			value={this.state} /> 
		);
	};
};

export default SearchPanel;
