import React, { Component } from 'react';
import TodoList from '../components/TodoList/TodoList';
import AppHeader from '../components/AppHeader/AppHeader';
import SearchPanel from '../components/SearchPanel/SearchPanel';
import ItemStatusFilter from '../components/ItemStatusFilter/ItemStatusFilter';
import ItemAddForm from '../components/ItemAddForm/ItemAddForm';
import './App.css'

class App extends Component {

  maxId = 100;

  state = {
    todoData:[
      this.createTodoItem('Drink Cofee'),
      this.createTodoItem('Make Awesome App'),
      this.createTodoItem('Have a Lunch')
    ]
  };

  createTodoItem (label) {
    return {
      label,
      important: false,
      done: false,
      id: this.maxId++
    }
  }

  deleteItem = (id) => {
    this.setState(({ todoData }) => {
      const indx = todoData.findIndex((el) => el.id === id );

      const newArray = [ 
        ...todoData.slice(0,indx), 
        ...todoData.slice(indx +1)
      ];

      return {
				todoData: newArray
			};
		});
  }

  addItem = (text) => {
    const newItem = this.createTodoItem(text);

    this.setState (({todoData}) => {
      const newArr = [
        ...todoData,
        newItem
      ];

      return {
        todoData: newArr
      }
    });
  };

  toggleItemDone = (id) => {
    this.setState(({todoData}) => {
      const indx = todoData.findIndex((el) => el.id === id );

      const oldItem = todoData[indx];
      const newItem = { ...oldItem, done: !oldItem.done };

      const newArray = [ 
        ...todoData.slice(0,indx), 
        newItem,
        ...todoData.slice(indx +1)
      ];

      return {
				todoData: newArray
			};
    });
  };

  toggleItemImportant = (id) => {
    console.log('ToggleImportant',id )
  }

  render() {
    return (
      <div className="todo-app">
        <AppHeader toDo={1} done={3} />
        <div className="top-panel d-flex">
          <SearchPanel />
          <ItemStatusFilter />
        </div>

        <TodoList 
          todos={this.state.todoData}
          onDeleted={this.deleteItem}
          onToggleDone={this.toggleItemDone}
          onToggleImportant={this.toggleItemImportant}
          />
        <ItemAddForm onItemAdded={this.addItem}/>
      </div>
    );
  };
};




export default App;
